sys_script_include.script:
function VictimOffenderSearch() {
	var user = gs.getUserID();

	var victimGr = new GlideRecord('x_snc_offmgtcsc_victim');
	victimGr.get("true_user",user);
	var offender = victimGr.getValue('offender');

	var offenders = [];
	offenders.push(offender);
	return offenders;
}
